    private void btninsertarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btninsertarActionPerformed
        if (txtdescripcion.getText().length() == 0) {
            JOptionPane.showMessageDialog(rootPane, "DEBES INGRESAR LA DESCRIPCION");
            txtdescripcion.requestFocus();
            return;
        }
        if (txtpreciount.getText().length() == 0) {
            JOptionPane.showMessageDialog(rootPane, "DEBES INGRESAR EL PRODUCTO");
            txtpreciount.requestFocus();
            return;
        }
         if (txtmonto.getText().length() == 0) {
            JOptionPane.showMessageDialog(rootPane, "DEBES INGRESAR EL MONTO");
            txtmonto.requestFocus();
            return;
        }

        vventas dts = new vventas();
        fventa func = new fventa();

//        dts.setIdusuarios(Integer.parseInt(frmprincipal.lblcodusuario.getText()));
//        dts.set(txtdescripcion.getText());
        Long deuda = Long.parseLong(txtdeuda.getText());
        Long monto = Long.parseLong(txtmonto.getText());
        Long total = deuda - monto;
        dts.setTotal(total);

        if (accion.equals("Editar") && total <= 0) {
            if (monto > deuda) {
                    JOptionPane.showMessageDialog(panelboton2, "El monto a pagar no puede ser mayor a la deuda");
                }else{
                
            dts.setIdventa(Integer.parseInt(txtid.getText()));
            dts.setTotal(monto + (Long.parseLong(txttotal.getText())));
            dts.setSaldo(total);
            dts.setEstado("FINALIZADO");
//              dts.setIdmovimiento(Integer.parseInt(txtidmovimiento.getText()));
            func.editar(dts);
            JOptionPane.showMessageDialog(this, "Pago total realizado con exito");
            mostrar(txtid.getText());

//          JOptionPane.showMessageDialog(panelboton2, "no sobra saldo "+total);
            cancelar();
            }


        } else {
            if (total  > 0) {
                if (monto > deuda) {
                    JOptionPane.showMessageDialog(panelboton2, "El monto a pagar no puede ser mayor a la deuda");
                }else{
//                      JOptionPane.showMessageDialog(panelboton2, "sobra saldo "+total);
                dts.setIdventa(Integer.parseInt(txtid.getText()));
                dts.setTotal(monto + (Long.parseLong(txttotal.getText())));
                dts.setSaldo(total);
                dts.setEstado("PENDIENTE");
//                dts.setTotal(Long.parseLong(txttotal.getText()));
                func.editar(dts);
               
                JOptionPane.showMessageDialog(this, "Pago parcial realizado con exito");
                mostrar(frmDeudas.txtdocumento.getText());
//                mostrar(txtid.getText()); 
                }
//           
            }
        }

        cancelar();
    }//GEN-LAST:event_btninsertarActionPerformed

