    private void tablalistadoMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tablalistadoMouseClicked
        accion = "Editar";
        btninsertar.setText("Editar");
        nuevo();
        try {
//             registro[0] = rs.getString("idventa");
//                registro[1] = rs.getString("nombre_producto");
//                registro[2] = rs.getString("cantidad");
//                registro[3] = rs.getString("precio");
//                registro[4] = rs.getString("pulgadas");
//                registro[5] = rs.getString("estado");
//                registro[6] = rs.getString("tipo");
//                registro[7] = rs.getString("saldo");

            int fila = tablalistado.rowAtPoint(evt.getPoint());
            txtid.setText(tablalistado.getValueAt(fila, 0).toString());
            txtdescripcion.setText(tablalistado.getValueAt(fila, 1).toString());
            txtpreciount.setText(tablalistado.getValueAt(fila, 3).toString());
            txtdeuda.setText(tablalistado.getValueAt(fila, 7).toString());
            txttotal.setText(tablalistado.getValueAt(fila, 8).toString());
//            txtidmovimiento.setText(tablalistado.getValueAt(fila, 6).toString());

        } catch (Exception e) {
        }
    }//GEN-LAST:event_tablalistadoMouseClicked

