    void inhabilitar() {
        txtidcomida.setVisible(false);

        txtncomida.setEnabled(false);
        txtcantidad.setEnabled(false);
        txtcalorias.setEnabled(false);
        txtproteinas.setEnabled(false);
        txtvitaminas.setEnabled(false);
        txtminerales.setEnabled(false);

        btnguardar.setEnabled(false);
        btncancelar.setEnabled(false);
        btneliminar.setEnabled(false);
         btnnuevo.setEnabled(true);

        txtncomida.setText("");
        txtcantidad.setText("");
        txtcalorias.setText("");
        txtproteinas.setText("");
        txtvitaminas.setText("");
        txtminerales.setText("");

    }

    void habilitar() {
        txtidcomida.setVisible(false);

        txtncomida.setEnabled(true);
        txtcantidad.setEnabled(true);
        txtcalorias.setEnabled(true);
        txtproteinas.setEnabled(true);
        txtvitaminas.setEnabled(true);
        txtminerales.setEnabled(true);

        btnguardar.setEnabled(true);
        btncancelar.setEnabled(true);
        btneliminar.setEnabled(true);
        btnnuevo.setEnabled(false);
        txtncomida.setText("");
        txtcantidad.setText("");
        txtcalorias.setText("");
        txtproteinas.setText("");
        txtvitaminas.setText("");
        txtminerales.setText("");

    }
     void jtable(){
         //metodo para no editar el jtable
                for (int c = 0; c < tablalistado.getColumnCount(); c++)

        {
 
            Class<?> col_class = tablalistado.getColumnClass(c);

            tablalistado.setDefaultEditor(col_class, null); // remove editor

        }
     }
    void mostrar(String buscar) {
        try {
            DefaultTableModel modelo;
            falimento func = new falimento();
            modelo = func.mostrar(buscar);

            tablalistado.setModel(modelo);
            //ocultar_columnas();
            lbltotalregistros.setText("Total Registros: " + Integer.toString(func.totalRegistro));
            //lbltotalvalor.setText("Total valor: " + Integer.toString(func.totalvalor));

        } catch (Exception e) {
            JOptionPane.showConfirmDialog(rootPane, e);
        }
    }

