    private void tablalistadoMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tablalistadoMouseClicked
        btnguardar.setText("Editar");
        habilitar();
        btneliminar.setEnabled(true);
        accion = "editar";

        int fila = tablalistado.rowAtPoint(evt.getPoint());

        txtidcomida.setText(tablalistado.getValueAt(fila, 0).toString());
        txtncomida.setText(tablalistado.getValueAt(fila, 1).toString());

        txtcantidad.setText(tablalistado.getValueAt(fila, 2).toString());
        txtcalorias.setText(tablalistado.getValueAt(fila, 3).toString());
        txtproteinas.setText(tablalistado.getValueAt(fila, 4).toString());
        txtvitaminas.setText(tablalistado.getValueAt(fila, 5).toString());
        txtminerales.setText(tablalistado.getValueAt(fila, 6).toString());


    }//GEN-LAST:event_tablalistadoMouseClicked

