    private void cboestadoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cboestadoActionPerformed
        // TODO add your handling code here:
        if(cboestado.getSelectedItem().toString().equals("ACTIVO")){
            jFechaCierre.setEnabled(false);
        }
        if(cboestado.getSelectedItem().toString().equals("DESACTIVO")){
            jFechaCierre.setEnabled(false);
        }
        if(cboestado.getSelectedItem().toString().equals("CERRADO")){
            jFechaCierre.setEnabled(true);
        }
    }//GEN-LAST:event_cboestadoActionPerformed

