    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        for (int i = 0; i < tablalistado1.getRowCount(); i++) {
            String Datos[] = new String[14];

            Datos[0] = tablalistado1.getValueAt(i, 0).toString();
            Datos[1] = tablalistado1.getValueAt(i, 1).toString();
            Datos[2] = tablalistado1.getValueAt(i, 2).toString();
            Datos[3] = tablalistado1.getValueAt(i, 3).toString();
            Datos[4] = tablalistado1.getValueAt(i, 4).toString();
            Datos[5] = tablalistado1.getValueAt(i, 5).toString();
            Datos[6] = tablalistado1.getValueAt(i, 6).toString();
            Datos[7] = tablalistado1.getValueAt(i, 7).toString();
            Datos[8] = tablalistado1.getValueAt(i, 8).toString();
            Datos[9] = tablalistado1.getValueAt(i, 9).toString();
            Datos[10] = tablalistado1.getValueAt(i, 10).toString();
            Datos[11] = tablalistado1.getValueAt(i, 11).toString();
            Datos[12] = tablalistado1.getValueAt(i, 12).toString();
            Datos[13] = tablalistado1.getValueAt(i, 13).toString();

            frmcargar_dieta.modelo2.addRow(Datos);

        }
        vaciartabla();
        this.dispose();
    }//GEN-LAST:event_jButton2ActionPerformed

